      const originalForecastMean = point.forecast_mean ?? 0;
      const clampedForecastMean = Math.max(0, originalForecastMean);
      let clampedConfidenceRange: [number, number] | undefined = undefined;
      if (point.forecast_confidence_range) {
        const [low, high] = point.forecast_confidence_range;
        clampedConfidenceRange = [Math.max(0, low), Math.max(0, high)];
        if (low < 0 || high < 0) {
          console.warn(
            `Negative forecast_confidence_range detected and clamped to 0 for data point:`,
            { ...point }
          );
        }
      }
      if (originalForecastMean < 0) {
        console.warn(
          `Negative forecast_mean detected and clamped to 0 for data point:`,
          { ...point }
