        value: clampedForecastMean, // Clamp negative values to 0
        forecast_mean: clampedForecastMean, // Also clamp forecast_mean
        forecast_confidence_range: clampedConfidenceRange,
        historical_mean: undefined,
        is_forecast: true
      };
    });
